<script>
    import { ref, onMounted } from 'vue';
    import { ElMessage } from 'element-plus';
    import { Check, Lock } from '@element-plus/icons-vue';
    import { getAdminInfo, updateAdmin, updateAdminPassword } from '@/api/admin';

    export default {
        setup() {
            // Admin information form
            const profileForm = ref({
                adminName: '',
                nickName: '',
                phoneNumber: '',
            });

            // Password change form
            const passwordForm = ref({
                oldPassword: '',
                newPassword: '',
                confirmPassword: '',
            });

            // Form validation rules
            const rules = ref({
                nickName: [
                    { required: true, message: 'Please enter a nickname', trigger: 'blur' },
                    { min: 2, max: 18, message: 'Nickname length must be between 2 and 18 characters', trigger: 'blur' },
                ],
                phoneNumber: [
                    { required: true, message: 'Please enter a phone number', trigger: 'blur' },
                    {
                        pattern: /^1[3-9]\d{9}$/,
                        message: 'Please enter a valid phone number',
                        trigger: 'blur',
                    },
                ],
            });

            // Password change validation rules
            const passwordRules = ref({
                oldPassword: [
                    { required: true, message: 'Please enter current password', trigger: 'blur' },
                ],
                newPassword: [
                    { required: true, message: 'Please enter new password', trigger: 'blur' },
                    {
                        pattern: /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,20}$/,
                        message: 'Password must contain letters and numbers, length 8-20 characters',
                        trigger: 'blur',
                    },
                ],
                confirmPassword: [
                    { required: true, message: 'Please confirm new password', trigger: 'blur' },
                    {
                        validator: (rule, value, callback) => {
                            if (value !== passwordForm.value.newPassword) {
                                callback(new Error('The two passwords do not match'));
                            } else {
                                callback();
                            }
                        },
                        trigger: 'blur',
                    },
                ],
            });

            // Form references
            const profileFormRef = ref(null);
            const passwordFormRef = ref(null);

            // Password dialog visibility status
            const passwordDialogVisible = ref(false);

            // Loading status
            const updateLoading = ref(false);

            // Get admin information
            const fetchAdminInfo = async () => {
                try {
                    const res = await getAdminInfo();
                    if (res.code === 200) {
                        profileForm.value = res.data;
                    } else {
                        ElMessage.error('Failed to get information');
                    }
                } catch (error) {
                    console.error('Failed to get information:', error);
                    ElMessage.error('Failed to get information');
                }
            };

            // Handle save changes
            const handleUpdate = async () => {
                try {
                    updateLoading.value = true;
                    await profileFormRef.value.validate();
                    const res = await updateAdmin(profileForm.value);
                    if (res.code === 200) {
                        ElMessage.success('Changes saved successfully');
                    } else {
                        ElMessage.error('Failed to save changes');
                    }
                } catch (error) {
                    console.error('Failed to save changes:', error);
                    ElMessage.error('Failed to save changes');
                } finally {
                    updateLoading.value = false;
                }
            };

            // Show password change dialog
            const showPasswordDialog = () => {
                passwordDialogVisible.value = true;
            };

            // Handle password change submission
            const handlePasswordSubmit = async () => {
                try {
                    await passwordFormRef.value.validate();
                    const res = await updateAdminPassword({
                        oldPwd: passwordForm.value.oldPassword,
                        newPwd: passwordForm.value.newPassword,
                    });
                    if (res.code === 200) {
                        ElMessage.success('Password changed successfully');
                        passwordDialogVisible.value = false;
                        passwordFormRef.value.resetFields();
                    } else {
                        ElMessage.error(res.message || 'Failed to change password');
                    }
                } catch (error) {
                    console.error('Failed to change password:', error);
                    ElMessage.error(error.response?.data?.message || 'Failed to change password');
                }
            };

            // Get admin information when component is mounted
            onMounted(() => {
                fetchAdminInfo();
            });

            return {
                profileForm,
                passwordForm,
                rules,
                passwordRules,
                profileFormRef,
                passwordFormRef,
                passwordDialogVisible,
                updateLoading,
                handleUpdate,
                showPasswordDialog,
                handlePasswordSubmit,
                Check,
                Lock,
            };
        },
    };
</script>

